import React from 'react';
import uuid from 'uuid';
import Cards from './components/Cards';

export default class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      cards: [
        {
            id: uuid.v4(),
            text: 'first caard'
        },
        {
            id: uuid.v4(),
            text: 'second card'
        }  
      ]
    };
  }
  render() {
    const {cards} = this.state;

    return (
      <div>
        <Cards cards = {cards} />
        <button onClick = {() => console.log('add card')}>+</button>
      </div>
    );
  }
}
